    function isSupportedPair(address asset, address base)
        public
        view
        virtual
        returns (bool)
    {}

    function assetPrice(
        address asset,
        address base,
        uint256 amount
    ) public view virtual returns (uint256) {}

    function name() external virtual returns (string memory);

    uint256[50] private __gap;
