// SPDX-License-Identifier: GPL-3.0

pragma solidity 0.8.7;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@uniswap/v3-periphery/contracts/interfaces/IPeripheryImmutableState.sol";
import "@uniswap/swap-router-contracts/contracts/interfaces/IV3SwapRouter.sol";
import "@chainlink/contracts/src/v0.8/Denominations.sol";

import "../lib/BytesLib.sol";
import "../lib/Constant.sol";

contract UniswapSwapper is Initializable {
    using BytesLib for bytes;

    address public constant RECIPIENT_EXECUTEE = address(1);
    address public constant RECIPIENT_UNISWAP_ROUTER = address(2);

    enum MulticallResultAttribute { EMPTY, AMOUNT_IN, AMOUNT_OUT }

    struct MulticallData {
        address recipient;
        address tokenIn;
        address tokenOut; 
        uint256 amountIn; 
        uint256 amountOut; 
        MulticallResultAttribute resultType;
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
      _disableInitializers();
    }

    function WETH9() public pure returns (address) {
        return Constant.WETH_ADDRESS;
    }

    function decodeWETH9Call(bytes memory data, uint256 value) external pure returns(address tokenIn, address tokenOut, uint256 amount) {
        bytes4 funcSig = data.toBytes4(0);
        if (funcSig == bytes4(keccak256("deposit()"))) {
            return (Denominations.ETH, WETH9(), value);
        } else if (funcSig == bytes4(keccak256("withdraw(uint256)"))) {
            return (WETH9(), Denominations.ETH, abi.decode(data.slice(4, data.length - 4), (uint256)));
        }

        revert("Failed to decode Uniswap bytecode");
    }

    function decodeUniswapMulticall(bytes memory rawData, uint256 value, bytes memory response) external view returns(MulticallData[] memory multicalData) {
        bytes[] memory executions = _decodeMulticall(rawData);
        bytes[] memory executionResults;
        uint256 remainEth = value;

        multicalData = new MulticallData[](executions.length);

        if (response.length != 0) {
            executionResults = abi.decode(response, (bytes[]));
        } 

        for (uint i = 0; i < executions.length; i++) {
            (bool success, bytes memory rawSwapData) = address(this).staticcall(executions[i]);
            require(success, "fail to decode uniswap multicall");

            MulticallData memory swapData = abi.decode(rawSwapData, (MulticallData));

            if (swapData.tokenIn == WETH9() && remainEth != 0) {
                require(swapData.amountIn <= remainEth, "fail to decode WETH swap data");
                swapData.tokenIn = Denominations.ETH;
                remainEth -= swapData.amountIn;
            }
            if (executionResults.length != 0) {
                if (swapData.resultType == MulticallResultAttribute.AMOUNT_IN) {
                    swapData.amountIn = abi.decode(executionResults[i], (uint256));
                } else if (swapData.resultType == MulticallResultAttribute.AMOUNT_OUT) {
                    swapData.amountOut = abi.decode(executionResults[i], (uint256));
                }
            }
            multicalData[i] = swapData;
        }

        require(remainEth == 0, "passing too much ETH to uniswap");
    }

    function _decodeMulticall(bytes memory _data) internal pure returns (bytes[] memory executions) {
        bytes4 funcSig = _data.toBytes4(0);
        if (funcSig == bytes4(keccak256("multicall(uint256,bytes[])"))) {
            (, executions) = abi.decode(_data.slice(4, _data.length - 4), (uint256, bytes[]));
        } else if (funcSig == bytes4(keccak256("multicall(bytes32,bytes[])"))) {
            (, executions) = abi.decode(_data.slice(4, _data.length - 4), (bytes32, bytes[]));
        } else {
           revert("Failed to decode Uniswap multicall bytecode");
        }
    }

    // From Uniswap/swap-router-contracts/contracts/V3SwapRouter.sol
    function exactOutputSingle(
        IV3SwapRouter.ExactOutputSingleParams calldata params
    ) external pure returns (MulticallData memory) {
        return MulticallData({
            recipient: params.recipient,
            tokenIn: params.tokenIn,
            tokenOut: params.tokenOut,
            amountIn: params.amountInMaximum,
            amountOut: params.amountOut,
            resultType: MulticallResultAttribute.AMOUNT_IN
        });
    }
    // From Uniswap/swap-router-contracts/contracts/V3SwapRouter.sol
    function exactInputSingle(
        IV3SwapRouter.ExactInputSingleParams calldata params
    ) external pure returns (MulticallData memory) {
        return MulticallData({
            recipient: params.recipient,
            tokenIn: params.tokenIn,
            tokenOut: params.tokenOut,
            amountIn: params.amountIn,
            amountOut: params.amountOutMinimum,
            resultType: MulticallResultAttribute.AMOUNT_OUT
        });
    }

    // From Uniswap/swap-router-contracts/contracts/V3SwapRouter.sol
    function exactOutput(
        IV3SwapRouter.ExactOutputParams calldata params
    ) external pure returns (MulticallData memory) {
        return MulticallData({
            recipient: params.recipient,
            tokenIn: params.path.toAddress(0),
            tokenOut: params.path.toAddress(params.path.length - 20),
            amountIn: params.amountInMaximum,
            amountOut: params.amountOut, 
            resultType: MulticallResultAttribute.AMOUNT_IN
        });
    }

    // From Uniswap/swap-router-contracts/contracts/V3SwapRouter.sol
    function exactInput(
        IV3SwapRouter.ExactInputParams calldata params
    ) external pure returns (MulticallData memory) {
        return MulticallData({
            recipient: params.recipient,
            tokenIn: params.path.toAddress(0),
            tokenOut: params.path.toAddress(params.path.length - 20),
            amountIn: params.amountIn,
            amountOut: params.amountOutMinimum, 
            resultType: MulticallResultAttribute.AMOUNT_OUT
        });
    }

    // From Uniswap/swap-router-contracts/contracts/V2SwapRouter.sol
    function swapTokensForExactTokens(
        uint256 amountOut,
        uint256 amountInMax,
        address[] calldata path,
        address recipient
    ) external pure returns (MulticallData memory) {
        return MulticallData({
            recipient: recipient,
            tokenIn: path[0],
            tokenOut: path[path.length - 1],
            amountIn: amountInMax,
            amountOut: amountOut, 
            resultType: MulticallResultAttribute.AMOUNT_IN
        });
    }

    // From Uniswap/swap-router-contracts/contracts/V2SwapRouter.sol
    function swapExactTokensForTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address recipient
    ) external pure returns (MulticallData memory) {
        return MulticallData({
            recipient: recipient,
            tokenIn: path[0],
            tokenOut: path[path.length - 1],
            amountIn: amountIn,
            amountOut: amountOutMin, 
            resultType: MulticallResultAttribute.AMOUNT_OUT
        });
    }

    function unwrapWETH9(
        uint256 amountMinimum,
        address recipient
    ) external pure returns (MulticallData memory) {
        return MulticallData({
            recipient: recipient,
            tokenIn: address(0),
            tokenOut: Denominations.ETH,
            amountIn: 0,
            amountOut: amountMinimum, 
            resultType: MulticallResultAttribute.EMPTY
        });
    }

    function refundETH() external pure returns (MulticallData memory) {
        return MulticallData({
            recipient: address(0),
            tokenIn: address(0),
            tokenOut: address(0),
            amountIn: 0,
            amountOut: 0,
            resultType: MulticallResultAttribute.EMPTY
        });
    }

    function selfPermit(
        address, uint256, uint256, uint8, bytes32, bytes32
    ) external pure returns (MulticallData memory) {
        return MulticallData({
            recipient: address(0),
            tokenIn: address(0),
            tokenOut: address(0),
            amountIn: 0,
            amountOut: 0, 
            resultType: MulticallResultAttribute.EMPTY
        });
    }

    function sweepToken(
        address token,
        uint256 amountMinimum,
        address recipient
    ) external pure returns (MulticallData memory) {
        return MulticallData({
            recipient: recipient,
            tokenIn: address(0),
            tokenOut: token,
            amountIn: 0,
            amountOut: amountMinimum, 
            resultType: MulticallResultAttribute.EMPTY
        });
    }

    uint256[50] private __gap;

}

contracts/base/PriceResolver.sol.content:
// SPDX-License-Identifier: GPL-3.0

pragma solidity 0.8.7;
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@chainlink/contracts/src/v0.8/Denominations.sol";
import "@chainlink/contracts/src/v0.8/interfaces/FeedRegistryInterface.sol";
import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "../lib/Constant.sol";

contract PriceResolver is Initializable {
    address private _baseCurrency;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
      _disableInitializers();
    }

    function __PriceResolver_init(address __baseCurrency) internal onlyInitializing {
        _baseCurrency = __baseCurrency;
    }

    function baseCurrency() public view virtual returns (address) {
        return _baseCurrency;
    }
    function _WETH9() internal pure returns (address) {
        return Constant.WETH_ADDRESS;
    }

    /// @notice This function is imported by other contract, thus cannot be external
    function assetBaseCurrencyPrice(address asset, uint256 amount) public view virtual returns (uint256) {
        address __baseCurrency = baseCurrency();
        if (asset == __baseCurrency)
            return amount;

        if(__baseCurrency == Denominations.ETH || __baseCurrency == _WETH9()) {
            return assetEthPrice(asset, amount);
        }

        if(asset == Denominations.ETH || asset == _WETH9()) {
            return ethAssetPrice(__baseCurrency, amount);
        }

        uint8 baseDecimals = baseCurrencyDecimals();
        int price = getDerivedPrice(asset, __baseCurrency, 18 /* ETH decimals */);

        if (price > 0) {
            return uint256(scalePrice(int256(price) * int256(amount), 18 + IERC20Metadata(asset).decimals(), baseDecimals));
        }
        return 0;
    }

    function ethAssetPrice(address asset, uint256 ethAmount) public view virtual returns (uint256) {
        if (asset == Denominations.ETH || asset == _WETH9())
            return ethAmount;

        (uint80 roundID, int price, , uint256 updatedAt, uint80 answeredInRound) = 
        FeedRegistryInterface(Constant.FEED_REGISTRY).latestRoundData(asset, Denominations.ETH);
        uint8 priceDecimals = FeedRegistryInterface(Constant.FEED_REGISTRY).decimals(asset, Denominations.ETH);

        require(answeredInRound >= roundID, "Stale price in Chainlink");
        require(block.timestamp <= updatedAt + Constant.STALE_PRICE_DELAY, "Stale price in Chainlink");

        price = scalePrice(price, priceDecimals, 18 /* ETH decimals */);
        if (price > 0) {
            return ethAmount * (10 ** IERC20Metadata(asset).decimals()) / uint256(price);
        }

        return 0;
    }

    function assetEthPrice(address asset, uint256 amount) public view virtual returns (uint256) {
        if (asset == Denominations.ETH || asset == _WETH9())
            return amount;

        (uint80 roundID, int price, , uint256 updatedAt, uint80 answeredInRound) = 
        FeedRegistryInterface(Constant.FEED_REGISTRY).latestRoundData(asset, Denominations.ETH);
        uint8 priceDecimals = FeedRegistryInterface(Constant.FEED_REGISTRY).decimals(asset, Denominations.ETH);

        require(answeredInRound >= roundID, "Stale price in Chainlink");
        require(block.timestamp <= updatedAt + Constant.STALE_PRICE_DELAY, "Stale price in Chainlink");

        price = scalePrice(price, priceDecimals, 18 /* ETH decimals */);

        if (price > 0) {
            return uint256(price) * amount / 10 ** IERC20Metadata(asset).decimals();
        }

        return 0;
    }

    function getDerivedPrice(address _base, address _quote, uint8 _decimals)
        internal
        view
        virtual
        returns (int256)
    {
        require(_decimals > uint8(0) && _decimals <= uint8(18), "Invalid _decimals");
        int256 decimals = int256(10 ** uint256(_decimals));
        (uint80 _baseRoundID, int basePrice, , uint256 _baseUpdatedAt, uint80 _baseAnsweredInRound) = 
            FeedRegistryInterface(Constant.FEED_REGISTRY).latestRoundData(_base, Denominations.ETH);

        require(_baseAnsweredInRound >= _baseRoundID, "Stale price in Chainlink 104");
        require(block.timestamp <= _baseUpdatedAt + Constant.STALE_PRICE_DELAY, "Stale price in Chainlink 105");

        uint8 baseDecimals = FeedRegistryInterface(Constant.FEED_REGISTRY).decimals(_base, Denominations.ETH);
        basePrice = scalePrice(basePrice, baseDecimals, _decimals);
        (uint80 _quoteRoundID, int quotePrice, , uint256 _quoteUpdatedAt, uint80 _quoteAnsweredInRound) = 
            FeedRegistryInterface(Constant.FEED_REGISTRY).latestRoundData(_quote, Denominations.ETH);
        require(_quoteAnsweredInRound >= _quoteRoundID, "Stale price in Chainlink 112");
        require(block.timestamp <= _quoteUpdatedAt + Constant.STALE_PRICE_DELAY, "Stale price in Chainlink 113");

        uint8 quoteDecimals = FeedRegistryInterface(Constant.FEED_REGISTRY).decimals(_quote, Denominations.ETH);
        quotePrice = scalePrice(quotePrice, quoteDecimals, _decimals);

        return basePrice * decimals / quotePrice;
    }

    function scalePrice(int256 _price, uint8 _priceDecimals, uint8 _decimals)
        internal
        pure
        virtual
        returns (int256)
    {
        if (_priceDecimals < _decimals) {
            return _price * int256(10 ** uint256(_decimals - _priceDecimals));
        } else if (_priceDecimals > _decimals) {
            return _price / int256(10 ** uint256(_priceDecimals - _decimals));
        }
        return _price;
    }

    function baseCurrencyDecimals() public view virtual returns (uint8) {
        if (baseCurrency() == Denominations.ETH) return 18;
        try IERC20Metadata(baseCurrency()).decimals() returns (uint8 _decimals) {
            return _decimals;
        } catch {
            return 0;
        }
    }

    /// @notice This function is imported by other contract, thus cannot be external
    function canResolvePrice(address asset) public view virtual returns (bool) {
        if (asset == Denominations.ETH || asset == _WETH9())
            return true;
        try FeedRegistryInterface(Constant.FEED_REGISTRY).getFeed(asset, Denominations.ETH) {
            return true;
        } catch (bytes memory) {
            return false;
        }
    }

    uint256[50] private __gap;
}

