// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.7;

contract MockWETH9 {
    string public name     = "Wrapped Ether";
    string public symbol   = "WETH";
    uint8  public decimals = 18;

    mapping (address => uint)                       public  balanceOf;
    mapping (address => mapping (address => uint))  public  allowance;

    function mint(address to, uint256 amount) public {
        balanceOf[to] += amount;
    }

    function deposit() public payable {
        balanceOf[msg.sender] += msg.value;
    }
    function withdraw(uint wad) public {
        require(balanceOf[msg.sender] >= wad);
        balanceOf[msg.sender] -= wad;
        payable(msg.sender).transfer(wad);
    }

    function totalSupply() public view returns (uint) {
        return address(this).balance;
    }

    function approve(address guy, uint wad) public returns (bool) {
        allowance[msg.sender][guy] = wad;
        return true;
    }

    function transfer(address dst, uint wad) public returns (bool) {
        return transferFrom(msg.sender, dst, wad);
    }

    function transferFrom(address src, address dst, uint wad)
        public
        returns (bool)
    {
        require(balanceOf[src] >= wad);

        if (src != msg.sender && allowance[src][msg.sender] != type(uint).max) {
            require(allowance[src][msg.sender] >= wad);
            allowance[src][msg.sender] -= wad;
        }

        balanceOf[src] -= wad;
        balanceOf[dst] += wad;

        return true;
    }
}

contracts/UniswapAnyTokenBudgetApproval..content:
// SPDX-License-Identifier: GPL-3.0

pragma solidity 0.8.7;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "./base/CommonBudgetApproval.sol";
import "./lib/BytesLib.sol";
import "./dex/UniswapSwapper.sol";
import "./lib/Constant.sol";

import "./base/PriceResolver.sol";
import "./interface/IDao.sol";
import "./interface/IAdam.sol";
import "./interface/IBudgetApprovalExecutee.sol";

contract UniswapAnyTokenBudgetApproval is CommonBudgetApproval, UniswapSwapper {

    using BytesLib for bytes;

    event AllowToToken(address token);
    event ExecuteUniswapInTransaction(uint256 indexed id, address indexed executor, address indexed toAddress, address token, uint256 amount);
    event ExecuteUniswapOutTransaction(uint256 indexed id, address indexed executor, address indexed toAddress, address token, uint256 amount);
    event ExecuteWETH9Transaction(uint256 indexed id, address indexed executor, address indexed toAddress, address tokenIn, address tokenOut, uint256 amount);

    string public constant override name = "Uniswap Any Token Budget Approval";

    bool public allowAllFromTokens;
    address public fromToken;
    bool public allowAllToTokens;
    mapping(address => bool) public toTokensMapping;
    bool public allowAnyAmount;
    uint256 public totalAmount;
    uint8 public amountPercentage;

    mapping(uint256 => mapping(address => uint256)) private _tokenInAmountOfTransaction;
    mapping(uint256 => address[]) private _tokenInOfTransaction;

    function initialize(
        InitializeParams calldata params,
        bool _allowAllFromTokens,
        address _fromToken,
        bool _allowAllToTokens,
        address[] calldata _toTokens,
        bool _allowAnyAmount,
        uint256 _totalAmount,
        uint8 _amountPercentage
    ) public initializer {
        __BudgetApproval_init(params);

        allowAllFromTokens = _allowAllFromTokens;
        if(!_allowAllFromTokens) {
            fromToken = _fromToken;
            emit AllowToken(_fromToken);
        }

        allowAllToTokens = _allowAllToTokens;
        for(uint i = 0; i < _toTokens.length; i++) {
            _addToToken(_toTokens[i]);
        }

        allowAnyAmount = _allowAnyAmount;
        totalAmount = _totalAmount;
        amountPercentage = _amountPercentage;

    }

    function afterInitialized() external override onlyExecutee {
         if(!allowAllFromTokens) {
            approveTokenForUniswap(fromToken);
        }
    }

    function approveTokenForUniswap(address _fromToken) public {

        address _executee = executee();

        require(msg.sender == _executee ||
          msg.sender == executor() ||
          ITeam(team()).balanceOf(msg.sender, executorTeamId()) > 0, "Executor not whitelisted in budget"
        );

        bytes memory data = abi.encodeWithSignature("approve(address,uint256)", Constant.UNISWAP_ROUTER, type(uint256).max);
        IBudgetApprovalExecutee(_executee).executeByBudgetApproval(_fromToken, data, 0);
    }

    function executeParams() external pure override returns (string[] memory) {
        string[] memory arr = new string[](3);
        arr[0] = "address to";
        arr[1] = "bytes data";
        arr[2] = "uint256 value";
        return arr;
    }

    function _execute(
        uint256 transactionId, 
        bytes memory data
    ) internal override {
        (address to, bytes memory executeData, uint256 value) = abi.decode(data,(address, bytes, uint256));

        if (to == Constant.UNISWAP_ROUTER) {
            _executeUniswapCall(transactionId, to, executeData, value);
        } else if (to == WETH9()) {
            _executeWETH9Call(transactionId, to, executeData, value);
        } else {
            revert("Invalid target address");
        }
    }

    function _executeUniswapCall(uint256 transactionId, address to, bytes memory executeData, uint256 value) private {
        address __executee = executee();

        bytes memory response = IBudgetApprovalExecutee(__executee).executeByBudgetApproval(to, executeData, value);
        MulticallData[] memory mDataArr = this.decodeUniswapMulticall(executeData, value, response);

        address[] storage _tokenIn = _tokenInOfTransaction[transactionId];
        mapping(address => uint256) storage _tokenInAmount = _tokenInAmountOfTransaction[transactionId];

        for (uint i = 0; i < mDataArr.length; i++) {
            MulticallData memory mData = mDataArr[i];

            require(mData.recipient == address(0) || 
                mData.recipient == RECIPIENT_EXECUTEE || 
                mData.recipient == RECIPIENT_UNISWAP_ROUTER || 
                mData.recipient == __executee, "Recipient not whitelisted");

            if (mData.amountIn > 0) {
                require(allowAllFromTokens || fromToken == mData.tokenIn, "Source token not whitelisted");

                if (_tokenInAmount[mData.tokenIn] == 0) {
                    _tokenIn.push(mData.tokenIn);
                }
                _tokenInAmount[mData.tokenIn] += mData.amountIn;

                emit ExecuteUniswapInTransaction(transactionId, msg.sender, Constant.UNISWAP_ROUTER, mData.tokenIn, mData.amountIn);
            }

            if (mData.amountOut > 0 && (mData.recipient == RECIPIENT_EXECUTEE || mData.recipient == __executee)) {
                require(allowAllToTokens || toTokensMapping[mData.tokenOut], "Target token not whitelisted");

                emit ExecuteUniswapOutTransaction(transactionId, msg.sender, Constant.UNISWAP_ROUTER, mData.tokenOut, mData.amountOut);
            }
        }

        if (!allowAnyAmount || amountPercentage < 100) {
            for (uint i = 0; i < _tokenIn.length; i++) {
                address tokenIn = _tokenIn[i];
                uint256 amount = _tokenInAmount[tokenIn];

                uint256 tokenInBalanceBeforeSwap;
                if(tokenIn == Denominations.ETH) {
                    tokenInBalanceBeforeSwap = __executee.balance + amount;
                } else {
                    tokenInBalanceBeforeSwap = IERC20(tokenIn).balanceOf(__executee) + amount;
                }

                require(allowAnyAmount || amount <= totalAmount, "Exceeded max amount");
                require(_checkAmountPercentageValid(tokenInBalanceBeforeSwap, amount), "Exceeded percentage");     

                if(!allowAnyAmount) {
                    totalAmount -= amount;
                }
            }
        }

    }

    function _executeWETH9Call(uint256 transactionId, address to, bytes memory executeData, uint256 value) private {
        address __executee = executee();

        IBudgetApprovalExecutee(__executee).executeByBudgetApproval(to, executeData, value);
        (
            address tokenIn,
            address tokenOut,
            uint256 amount
        ) = this.decodeWETH9Call(executeData, value);

        uint256 tokenInBalanceBeforeSwap;
        if(tokenIn == Denominations.ETH) {
            tokenInBalanceBeforeSwap = __executee.balance + amount;
        } else {
            tokenInBalanceBeforeSwap = IERC20(tokenIn).balanceOf(__executee) + amount;
        }

        require(allowAllFromTokens || fromToken == tokenIn, "Source token not whitelisted");
        require(allowAllToTokens || toTokensMapping[tokenOut], "Target token not whitelisted");
        require(allowAnyAmount || amount <= totalAmount, "Exceeded max amount");
        require(_checkAmountPercentageValid(tokenInBalanceBeforeSwap, amount), "Exceeded percentage");

        if(!allowAnyAmount) {
            totalAmount -= amount;
        }

        emit ExecuteWETH9Transaction(transactionId, msg.sender, WETH9(), tokenIn, tokenOut, amount);
    }

    function _checkAmountPercentageValid(uint256 balanceOfToken, uint256 amount) private view returns (bool) {
        if (amountPercentage == 100) return true;

        if (balanceOfToken == 0) return false;

        return amount <= balanceOfToken * amountPercentage / 100;
    }

    function _addToToken(address token) private {
        require(!toTokensMapping[token], "Duplicated token in target token list");
        toTokensMapping[token] = true;
        emit AllowToToken(token);
    }

}

contracts/LiquidPool.sol.content:
// SPDX-License-Identifier: GPL-3.0

pragma solidity 0.8.7;
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/IERC20MetadataUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";

import "./base/BudgetApprovalExecutee.sol";

import "@chainlink/contracts/src/v0.8/Denominations.sol";

import "./base/PriceResolver.sol";
import "./lib/Concat.sol";
import "./interface/IDao.sol";

contract LiquidPool is Initializable, UUPSUpgradeable, ERC20Upgradeable, PriceResolver, BudgetApprovalExecutee {
    using Concat for string;
    using SafeERC20Upgradeable for IERC20MetadataUpgradeable;

    IDao public dao;
    address[] public assets;
    mapping(address => uint256) private _assetIndex;

    event AllowDepositToken(address token);
    event DisallowDepositToken(address token);
    event Deposit(address account, address token, uint256 depositAmount);

    modifier onlyGovern(string memory category) {
        IDao _dao = dao;
        require(
            (_dao.byPassGovern(msg.sender)) || msg.sender == _dao.govern(category),
            string("Dao: only Govern").concat(category));
        _;
    }

    modifier onlyDao() {
        require(msg.sender == address(dao), "not dao");
        _;
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
      _disableInitializers();
    }

    function initialize(
        address owner,
        address[] memory depositTokens,
        address _baseCurrency
    )
        public initializer
    {
        __ERC20_init("LiquidPool", "LP");
        __PriceResolver_init(_baseCurrency);
        dao = IDao(payable(owner));
        _addAssets(depositTokens);
        ___BudgetApprovalExecutee_init(IDao(payable(owner)).team());
    }

    function assetsShares(address asset, uint256 amount) public view returns (uint256) {
        uint256 _totalSupply = totalSupply();

        require(amount <= _totalSupply, "gt totalSupply");
        require(isAssetSupported(asset), "Asset not support");
        if (_totalSupply == 0) return 0;

        return _assetBalance(asset) * amount / _totalSupply;
    }

    function quote(uint256 amount) public view returns (uint256) {
        uint256 _totalSupply = totalSupply();

        if (_totalSupply == 0) return amount;
        return amount * _totalSupply / totalPrice();
    }

    function canAddAsset(address asset) public view returns (bool) {
        return canResolvePrice(asset);
    }

    function totalPrice() public view returns (uint256) {
        uint256 total;
        uint256 _assetsLength = assets.length;

        for (uint256 i = 0; i < _assetsLength; i++) {
            address _asset = assets[i];
            total += assetBaseCurrencyPrice(_asset,  _assetBalance(_asset));
        }
        return total;
    }

    function totalPriceInEth() public view returns (uint256) {
        uint256 total;
        uint256 _assetsLength = assets.length;

        for (uint256 i = 0; i < _assetsLength; i++) {
            address _asset = assets[i];
            total += assetEthPrice(_asset,  _assetBalance(_asset));
        }
        return total;
    }

    function isAssetSupported(address asset) public view returns (bool) {
        return _assetIndex[asset] > 0;
    }

    function deposit(address receiver) public payable {
        require(isAssetSupported(Denominations.ETH), "asset not support");

        uint256 _totalSupply = totalSupply();
        uint256 ethPriceInBaseCurrency = assetBaseCurrencyPrice(Denominations.ETH, msg.value);

        if (_totalSupply == 0) {
            _mint(receiver, ethPriceInBaseCurrency);
            _afterDeposit(receiver, ethPriceInBaseCurrency);
            emit Deposit(receiver, Denominations.ETH, msg.value);
            return;
        }
        uint256 total = totalPrice() - ethPriceInBaseCurrency;
        _mint(receiver, ethPriceInBaseCurrency * _totalSupply / total);

        _afterDeposit(receiver, ethPriceInBaseCurrency);

        emit Deposit(receiver, Denominations.ETH, msg.value);
    }

    function redeem(uint256 amount) public {
        IDao _dao = dao;
        require(balanceOf(msg.sender) >= amount, "not enough balance");
        require(_dao.firstDepositTime(msg.sender) + _dao.locktime() <= block.timestamp, "lockup time");

        uint256 _assetsLength = assets.length;
        for (uint256 i = 0; i < _assetsLength; i++) {
            address _asset = assets[i];
            _transferAsset(msg.sender, _asset, assetsShares(_asset, amount));
        }
        _burn(msg.sender, amount);
    }

    function depositToken(address receiver, address asset, uint256 amount) public {
        require(isAssetSupported(asset), "Asset not support");
        require(IERC20MetadataUpgradeable(asset).allowance(msg.sender, address(this)) >= amount, "not approve");

        uint256 assetPriceInBaseCurrency = assetBaseCurrencyPrice(asset, amount);
        _mint(receiver, quote(assetPriceInBaseCurrency));
        IERC20MetadataUpgradeable(asset).safeTransferFrom(msg.sender, address(this), amount);
        _afterDeposit(receiver, assetPriceInBaseCurrency);

        emit Deposit(receiver, asset, amount);
    }

    function addAssets(address[] calldata erc20s) public onlyGovern("General") {
        _addAssets(erc20s);
    }

    function removeAssets(address[] calldata erc20s) public onlyGovern("General") {
        _removeAssets(erc20s);
    }

    function _beforeCreateBudgetApproval(address budgetApproval) internal view override onlyGovern("General") {
        require(dao.canCreateBudgetApproval(budgetApproval), "not whitelist");
    }

    function _beforeRevokeBudgetApproval(address budgetApproval) internal view override onlyGovern("General") {}

    function _assetBalance(address asset) internal view returns (uint256) {
        if(asset == Denominations.ETH) {
            return address(this).balance;
        }

        return IERC20MetadataUpgradeable(asset).balanceOf(address(this));
    }

    function _transferAsset(address target, address asset, uint256 amount) internal {
        if(asset == Denominations.ETH) {
            (bool success, ) = payable(target).call{ value: amount }("");
            require(success, "Failed to send Ether");
        } else {
            IERC20MetadataUpgradeable(asset).safeTransfer(target, amount);
        }
    }

    function _afterDeposit(address account, uint256 amount) private {
      dao.afterDeposit(account, amount);
    }

    function _addAssets(address[] memory erc20s) internal {
        for (uint256 i = 0; i < erc20s.length; i++) {
            _addAsset(erc20s[i]);
        }
    }

    function _removeAssets(address[] memory erc20s) internal {
        for (uint256 i = 0; i < erc20s.length; i++) {
            _removeAsset(erc20s[i]);
        }
    }

    function _addAsset(address erc20) internal {
        require(canAddAsset(erc20) && !isAssetSupported(erc20), "Asset not support");
        assets.push(erc20);
        _assetIndex[erc20] = assets.length;

        emit AllowDepositToken(erc20);
    }

    function _removeAsset(address erc20) internal {
        require(isAssetSupported(erc20), "Asset not in list");
        uint256 index = _assetIndex[erc20] - 1;
        address lastEl = assets[assets.length - 1];
        assets[index] = lastEl;

        _assetIndex[lastEl] = index + 1;
        _assetIndex[erc20] = 0;
        assets.pop();
        emit DisallowDepositToken(erc20);
    }

    function assetsLength() public view returns(uint256) {
        return assets.length;
    }

    function _authorizeUpgrade(address) internal view override onlyDao {}
    receive() external payable {}

    uint256[50] private __gap;
}

