// SPDX-License-Identifier: GPL-3.0

pragma solidity 0.8.7;
import "@openzeppelin/contracts/proxy/ERC1967/ERC1967Proxy.sol";
import "../interface/ICommonBudgetApproval.sol";

import "../lib/RevertMsg.sol";
import "../lib/Concat.sol";

contract MockBudgetApprovalExecutee {
    using Concat for string;
    mapping(address => bool) public budgetApprovals;
    address public memberToken; 
    //todo workaround for AS-834 , need to remove it when split memeber token outflow BA
    event CreateBudgetApproval(address budgetApproval, bytes data);

    function executeByBudgetApproval(address _to, bytes memory _data, uint256 _value) external returns (bytes memory) {
        (bool success, bytes memory result) = _to.call{ value: _value }(_data);
        if(!success) {
            revert(string("BudgetApprovalExecutee: execution failed - ").concat(RevertMsg.ToString(result)));
        }

        return result;
    }

    function _beforeCreateBudgetApproval(address) internal {}

    function createBudgetApprovals(address[] memory _budgetApprovals, bytes[] memory data) public {
        require(_budgetApprovals.length == data.length, "input invalid");

        for(uint i = 0; i < _budgetApprovals.length; i++) {
            _beforeCreateBudgetApproval(_budgetApprovals[i]);
            ERC1967Proxy _budgetApproval = new ERC1967Proxy(_budgetApprovals[i], data[i]);
            budgetApprovals[address(_budgetApproval)] = true;
            emit CreateBudgetApproval(address(_budgetApproval), data[i]);

            ICommonBudgetApproval(address(_budgetApproval)).afterInitialized();
        }
    }

    function setMemberToken(address _memberToken) public {
        memberToken = _memberToken;
    }
    receive() external payable {

    }
}

contracts/DepositRewardBudgetApproval.so.content:
// SPDX-License-Identifier: GPL-3.0

pragma solidity 0.8.7;

import "./base/CommonBudgetApproval.sol";
import "./lib/BytesLib.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol";
import "@chainlink/contracts/src/v0.8/Denominations.sol";

import "./interface/IBudgetApprovalExecutee.sol";
import "./interface/ILiquidPool.sol";
import "./interface/IDao.sol";
import "./interface/IMembership.sol";

contract DepositRewardBudgetApproval is CommonBudgetApproval {
    using BytesLib for bytes;
    using AddressUpgradeable for address;

    string public constant override name = "Deposit Reward Budget Approval";

    address payable public liquidPool;
    address public token;
    bool public allowAnyAmount;
    uint256 public totalAmount;

    uint256 public referrerRewardAmount;
    uint256 public refereeRewardAmount;

    event Refer(uint256 indexed referee, uint256 indexed referrer);
    event ExecuteReferrerRewardTransaction(
        uint256 indexed id,
        address indexed executor,
        address indexed referrer,
        address referee,
        address _token,
        uint256 _referrerRewardAmount,
        uint256 _refereeRewardAmount
    );
    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        InitializeParams calldata params,
        address _liquidPool,
        address _token,
        bool _allowAnyAmount,
        uint256 _totalAmount,
        uint256 _referrerRewardAmount,
        uint256 _refereeRewardAmount
    ) external initializer {
        __BudgetApproval_init(params);

        require(_liquidPool != address(0), "invalid liquidPool");
        require(_token != address(0) && _token.isContract(), "invalid token");

        liquidPool = payable(_liquidPool);
        token = _token;
        allowAnyAmount = _allowAnyAmount;
        totalAmount = _totalAmount;

        referrerRewardAmount = _referrerRewardAmount;
        refereeRewardAmount = _refereeRewardAmount;
    }

    function executeParams() external pure override returns (string[] memory) {
        string[] memory arr = new string[](4);
        arr[0] = "address receiver";
        arr[1] = "address asset";
        arr[2] = "uint256 amount";
        arr[3] = "address referrer";
        return arr;
    }

    function _isExecutor(address) internal pure override returns (bool) {
        // anyone can call
        return true;
    }

    function _execute(uint256 transactionId, bytes memory data)
        internal
        override
    {
        (address _receiver, address _asset, uint256 _amount, address _referrer) = abi.decode(
            data,
            (address, address, uint256, address)
        );

        if (_asset == Denominations.ETH) {
            require(msg.value == _amount, "amount not match");
            deposit(_receiver);
        } else {
            depositToken(_receiver, _asset, _amount);

        }

        _issueReward(_receiver, _referrer);

        emit ExecuteReferrerRewardTransaction(
            transactionId,
            msg.sender,
            _referrer,
            _receiver,
            token,
            referrerRewardAmount,
            refereeRewardAmount
        );

    }

    function _issueReward(address referee, address referrer) internal {
        uint256 _totalAmount = totalAmount;
        uint256 _referrerRewardAmount = referrerRewardAmount;
        uint256 _refereeRewardAmount = refereeRewardAmount;
        bool _allowAnyAmount = allowAnyAmount;
        address _executee = executee();
        address _token = token;

        address dao = ILiquidPool(liquidPool).dao();
        address membership = IDao(payable(dao)).membership();

        require(_allowAnyAmount || _totalAmount >= _referrerRewardAmount + _refereeRewardAmount, "not enough supply");
        require(!IMembership(membership).isMember(referee) && !IMembership(membership).wasMember(referee), "not qualify");

        if (!_allowAnyAmount) {
            totalAmount = _totalAmount - _referrerRewardAmount - _refereeRewardAmount;
        }

        if (_referrerRewardAmount > 0) {
            IBudgetApprovalExecutee(_executee).executeByBudgetApproval(
                _token,
                abi.encodeWithSelector(
                    IERC20.transfer.selector,
                    referrer,
                    _referrerRewardAmount
                ),
                0
            );
        }

        if (_refereeRewardAmount > 0) {
            IBudgetApprovalExecutee(_executee).executeByBudgetApproval(
                _token,
                abi.encodeWithSelector(
                    IERC20.transfer.selector,
                    referee,
                    _refereeRewardAmount
                ),
                0
            );
        }

    }

    function deposit(address receiver) internal {
        ILiquidPool(liquidPool).deposit{value: msg.value}(receiver);
    }

    function depositToken(address receiver, address asset, uint256 amount) internal {
        address payable _liquidPool = liquidPool;

        IERC20(asset).transferFrom(msg.sender, address(this), amount);
        IERC20(asset).approve(_liquidPool, amount);

        ILiquidPool(_liquidPool).depositToken(receiver, asset, amount);
    }
}

contracts/TransferLiquidERC20BudgetAppro.content:
// SPDX-License-Identifier: GPL-3.0

pragma solidity 0.8.7;

import "./base/CommonBudgetApproval.sol";
import "./lib/BytesLib.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./base/PriceResolver.sol";
import "@chainlink/contracts/src/v0.8/Denominations.sol";

import "./interface/IBudgetApprovalExecutee.sol";

contract TransferLiquidERC20BudgetApproval is
    CommonBudgetApproval,
    PriceResolver
{
    using BytesLib for bytes;

    string public constant override name =
        "Transfer Liquid ERC20 Budget Approval";

    bool public allowAllAddresses;
    mapping(address => bool) public addressesMapping;
    address[] public tokens;
    mapping(address => bool) public tokensMapping;
    bool public allowAnyAmount;
    uint256 public totalAmount;

    // v2
    uint256[] public toTeamIds;
    mapping(uint256 => bool) public toTeamIdsMapping;

    event AllowTeam(uint256 indexed teamId);
    event ExecuteTransferLiquidERC20Transaction(
        uint256 indexed id,
        address indexed executor,
        address indexed toAddress,
        address token,
        uint256 amount
    );

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        InitializeParams calldata params,
        bool _allowAllAddresses,
        address[] memory _toAddresses,
        address[] memory _tokens,
        bool _allowAnyAmount,
        uint256 _totalAmount,
        address _baseCurrency,
        // v2
        uint256[] memory _toTeamIds
    ) external initializer {
        __BudgetApproval_init(params);

        allowAllAddresses = _allowAllAddresses;
        for (uint256 i = 0; i < _toAddresses.length; i++) {
            _addToAddress(_toAddresses[i]);
        }

        for (uint256 i = 0; i < _tokens.length; i++) {
            _addToken(_tokens[i]);
        }

        for (uint256 i = 0; i < _toTeamIds.length; i++) {
            _addToTeam(_toTeamIds[i]);
        }

        allowAnyAmount = _allowAnyAmount;
        totalAmount = _totalAmount;
        __PriceResolver_init(_baseCurrency);
    }

    function executeParams() external pure override returns (string[] memory) {
        string[] memory arr = new string[](3);
        arr[0] = "address token";
        arr[1] = "address to";
        arr[2] = "uint256 value";
        return arr;
    }

    function _execute(uint256 transactionId, bytes memory data)
        internal
        override
    {
        (address token, address to, uint256 value) = abi.decode(
            data,
            (address, address, uint256)
        );
        uint256 amountInBaseCurrency;
        uint256 _totalAmount = totalAmount;
        bool _allowAnyAmount = allowAnyAmount;

        if (token == Denominations.ETH) {
            IBudgetApprovalExecutee(executee()).executeByBudgetApproval(
                to,
                "",
                value
            );
        } else {
            bytes memory executeData = abi.encodeWithSelector(
                IERC20.transfer.selector,
                to,
                value
            );
            IBudgetApprovalExecutee(executee()).executeByBudgetApproval(
                token,
                executeData,
                0
            );
        }

        amountInBaseCurrency = assetBaseCurrencyPrice(token, value);
        require(
            allowAllAddresses ||
                addressesMapping[to] ||
                _checkIsToTeamsMember(to),
            "Recipient not whitelisted in budget"
        );
        require(tokensMapping[token], "Token not whitelisted in budget");
        require(amountInBaseCurrency > 0, "Transfer amount should not be zero");
        require(
            _allowAnyAmount || amountInBaseCurrency <= _totalAmount,
            "Exceeded max budget transferable amount"
        );

        if (!_allowAnyAmount) {
            totalAmount = _totalAmount - amountInBaseCurrency;
        }
        emit ExecuteTransferLiquidERC20Transaction(
            transactionId,
            msg.sender,
            to,
            token,
            value
        );
    }

    function _addToken(address token) internal {
        require(!tokensMapping[token], "Duplicated Item in source token list.");
        require(
            canResolvePrice(token),
            "Unresolvable token in target token list."
        );

        tokens.push(token);
        tokensMapping[token] = true;
        emit AllowToken(token);
    }

    function _addToAddress(address to) internal {
        require(
            !addressesMapping[to],
            "Duplicated address in target address list"
        );
        addressesMapping[to] = true;
        emit AllowAddress(to);
    }

    function tokensLength() public view returns (uint256) {
        return tokens.length;
    }

    function _checkIsToTeamsMember(address to) internal view returns (bool) {
        uint256 _toTeamIdsLength = toTeamIds.length;
        address[] memory toArray = new address[](_toTeamIdsLength);
        for (uint256 i = 0; i < _toTeamIdsLength; i++) {
            toArray[i] = to;
        }

        uint256[] memory balances = ITeam(team()).balanceOfBatch(
            toArray,
            toTeamIds
        );

        for (uint256 i = 0; i < balances.length; i++) {
            if (balances[i] > 0) {
                return true;
            }
        }
        return false;
    }

    function _addToTeam(uint256 teamId) internal {
        require(
            !toTeamIdsMapping[teamId],
            "Duplicated team in target team list"
        );
        toTeamIdsMapping[teamId] = true;
        toTeamIds.push(teamId);
        emit AllowTeam(teamId);
    }

    function toTeamsLength() public view returns (uint256) {
        return toTeamIds.length;
    }
}

