// SPDX-License-Identifier: GPL-3.0

pragma solidity ^0.8.0;
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts/proxy/ERC1967/ERC1967Proxy.sol";

import "./base/BudgetApprovalExecutee.sol";

import "@chainlink/contracts/src/v0.8/Denominations.sol";

import "./base/PriceResolver.sol";
import "./lib/Concat.sol";
import "hardhat/console.sol";
import "./interface/IDao.sol";

contract LiquidPool is Initializable, UUPSUpgradeable, ERC20Upgradeable, PriceResolver, BudgetApprovalExecutee {
    using Concat for string;

    IDao public dao;
    address[] public assets;
    mapping(address => bool) public isAssetSupported;

    event AllowDepositToken(address token);

    modifier onlyGovern(string memory category) {
        require(
            (dao.byPassGovern(msg.sender)) || msg.sender == dao.govern(category),
            string("Dao: only Govern").concat(category));
        _;
    }

    function initialize(
        address owner,
        address[] memory depositTokens,
        address baseCurrency
    )
        public initializer
    {
        __ERC20_init("LiquidPool", "LP");
        __PriceResolver_init(baseCurrency);
        dao = IDao(payable(owner));
        _addAssets(depositTokens); // todo
        team = dao.team();
    }

    function assetsShares(address asset, uint256 amount) public view returns (uint256) {
        require(amount <= totalSupply(), "gt totalSupply");
        require(isAssetSupported[asset], "Asset not support");
        if (totalSupply() == 0) return 0;

        return _assetBalance(asset) * amount / totalSupply();
    }

    function quote(uint256 amount) public view returns (uint256) {
        if (totalSupply() == 0) return amount;
        return (amount * 10 ** baseCurrencyDecimals()) / (totalPrice() * 10 ** baseCurrencyDecimals() / totalSupply());
    }

    function canAddAsset(address asset) public view returns (bool) {
        return canResolvePrice(asset);
    }

    function totalPrice() public view returns (uint256) {
        uint256 total;
        for (uint256 i = 0; i < assets.length; i++) {
            total += assetBaseCurrencyPrice(assets[i],  _assetBalance(assets[i]));
        }
        return total;
    }

    function totalPriceInEth() public view returns (uint256) {
        uint256 total;
        for (uint256 i = 0; i < assets.length; i++) {
            total += assetEthPrice(assets[i],  _assetBalance(assets[i]));
        }
        return total;
    }

    function deposit() public payable {
        require(isAssetSupported[Denominations.ETH], "asset not support");
        if (totalSupply() == 0) {
            _mint(msg.sender, assetBaseCurrencyPrice(Denominations.ETH, msg.value));
            _afterDeposit(msg.sender, assetBaseCurrencyPrice(Denominations.ETH, msg.value));
            return;
        }
        uint256 total = totalPrice() - assetBaseCurrencyPrice(Denominations.ETH, msg.value);
        _mint(msg.sender, (assetBaseCurrencyPrice(Denominations.ETH, msg.value) * 10 ** baseCurrencyDecimals()) / (total * 10 ** baseCurrencyDecimals() / totalSupply()));

        _afterDeposit(msg.sender, assetBaseCurrencyPrice(Denominations.ETH, msg.value));
    }

    function redeem(uint256 amount) public {
        require(balanceOf(msg.sender) >= amount, "not enough balance");
        require(dao.firstDepositTime(msg.sender) + dao.locktime() <= block.timestamp, "lockup time");

        for (uint256 i = 0; i < assets.length; i++) {
            _transferAsset(msg.sender, assets[i], assetsShares(assets[i], amount));
        }
        _burn(msg.sender, amount);
    }

    function depositToken(address asset, uint256 amount) public {
        require(isAssetSupported[asset], "Asset not support");
        require(IERC20Metadata(asset).allowance(msg.sender, address(this)) >= amount, "not approve");

        _mint(msg.sender, quote(assetBaseCurrencyPrice(asset, amount)));
        IERC20Metadata(asset).transferFrom(msg.sender, address(this), amount);
        _afterDeposit(msg.sender, assetBaseCurrencyPrice(asset, amount));
    }

    function addAssets(address[] calldata erc20s) public onlyGovern("General") {
        _addAssets(erc20s);
    }

    function _beforeCreateBudgetApproval(address budgetApproval) internal view override onlyGovern("General") {
        require(dao.canCreateBudgetApproval(budgetApproval), "not whitelist");
    }

    function _assetBalance(address asset) internal view returns (uint256) {
        if(asset == Denominations.ETH) {
            return address(this).balance;
        }

        return IERC20Metadata(asset).balanceOf(address(this));
    }

    function _transferAsset(address target, address asset, uint256 amount) internal {
        if(asset == Denominations.ETH) {
            payable(target).transfer(amount);
        } else {
            IERC20Metadata(asset).transfer(target, amount);
        }
    }

    function _afterDeposit(address account, uint256 amount) private {
        if (dao.firstDepositTime(account) == 0) {
            dao.setFirstDepositTime(account);

            require(amount >= dao.minDepositAmount(), "deposit amount not enough");

            if (dao.isMember(account)) {
                return;
            }
            if(dao.minTokenToAdmit() > 0 ){
                bytes4 sector = bytes4(keccak256("balanceOf(address)"));
                bytes memory data = abi.encodeWithSelector(sector, account);
                (, bytes memory result) = address(dao.admissionToken()).call(data);

                uint256 balance = abi.decode(result,(uint256));
                require(balance >= dao.minTokenToAdmit(), "Admission token not enough");
            }
            dao.mintMember(account);
    }

    }

    function _addAssets(address[] memory erc20s) internal {
        for (uint256 i = 0; i < erc20s.length; i++) {
            _addAsset(erc20s[i]);
        }
    }

    function _addAsset(address erc20) internal {
        require(canAddAsset(erc20) && !isAssetSupported[erc20], "Asset not support");
        assets.push(erc20);
        isAssetSupported[erc20] = true;

        emit AllowDepositToken(erc20);
    }

    function _authorizeUpgrade(address newImplementation) internal override initializer {}
    receive() external payable {}
}

contracts/TransferERC20BudgetApproval.so.content:
// SPDX-License-Identifier: GPL-3.0

pragma solidity ^0.8.0;

import "./base/CommonBudgetApproval.sol";
import "./lib/BytesLib.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./base/PriceResolver.sol";

import "./interface/IBudgetApprovalExecutee.sol";
import "./interface/IDao.sol";
import "./interface/IAdam.sol";
import "hardhat/console.sol";

contract TransferERC20BudgetApproval is CommonBudgetApproval {
    using BytesLib for bytes;

    string public constant override name = "Transfer ERC20 Budget Approval";

    bool public allowAllAddresses;
    mapping(address => bool) public addressesMapping;
    bool public allowAllTokens;
    address public token;
    bool public allowAnyAmount;
    uint256 public totalAmount;
    uint8 public amountPercentage;

    function initialize(
        InitializeParams calldata params,
        bool _allowAllAddresses,
        address[] memory _toAddresses,
        bool _allowAllTokens,
        address _token,
        bool _allowAnyAmount,
        uint256 _totalAmount,
        uint8 _amountPercentage
    ) public initializer {
        __BudgetApproval_init(params);

        allowAllAddresses = _allowAllAddresses;
        for(uint i = 0; i < _toAddresses.length; i++) {
            _addToAddress(_toAddresses[i]);
        }
        allowAllTokens = _allowAllTokens;
        token = _token;
        allowAnyAmount = _allowAnyAmount;
        totalAmount = _totalAmount;
        amountPercentage = _amountPercentage;
    }

    function executeParams() public pure override returns (string[] memory) {
        string[] memory arr = new string[](3);
        arr[0] = "address token";
        arr[1] = "address to";
        arr[2] = "uint256 value";
        return arr;
    }

    function _execute(
        bytes memory data
    ) internal override {
        (address _token, address to, uint256 value) = abi.decode(data,(address, address, uint256));
        bytes memory executeData = abi.encodeWithSelector(IERC20.transfer.selector, to, value);

        IBudgetApprovalExecutee(executee).executeByBudgetApproval(token, executeData, 0);

        require(allowAllAddresses || addressesMapping[to], "Recipient not whitelisted in budget");
        require(allowAllTokens || token == _token, "Token not whitelisted in budget");
        require(allowAnyAmount || value <= totalAmount, "Exceeded max budget transferable amount");
        require(checkAmountPercentageValid(value, _token), "Exceeded max budget transferable percentage");

        if(!allowAnyAmount) {
            totalAmount -= value;
        }
    }

    function checkAmountPercentageValid(uint256 amount, address _token) internal view returns (bool) {
        if (amountPercentage == 100) return true;

        uint256 _totalAmount = amount + IERC20(_token).balanceOf(executee);
        if (_totalAmount == 0) return false;

        return amount <= _totalAmount * amountPercentage / 100;
    }

    function _addToAddress(address to) internal {
        require(!addressesMapping[to], "duplicate token");
        addressesMapping[to] = true;
        emit AllowAddress(to);
    }
}

