// SPDX-License-Identifier: GPL-3.0

pragma solidity 0.8.7;
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@chainlink/contracts/src/v0.8/Denominations.sol";
import "@chainlink/contracts/src/v0.8/interfaces/FeedRegistryInterface.sol";

import "./base/PriceGateway.sol";
import "./lib/Constant.sol";

contract ArbitrumChainlinkPriceGateway is PriceGateway {
    /// @custom:oz-upgrades-unsafe-allow constructor
    string public constant override name = "Arbitrum Chainlink Price Gateway";

    error StaleRoundId(uint80 roundID, uint80 answeredInRound);
    error StaleTimestamp(uint256 currentTimeStamp, uint256 updatedAtTimeStamp);
    error InvaildDecimal(uint8 decimals);
    constructor() {
    }

    /// @notice inherited from PriceGateway, help to check the imported pair support or not
    /// @dev Custom price gateway is allowed, but need to implement priceGateway.sol and set by govern
    /// @param asset the asset token address, support ETH , WETH and other ERC20
    /// @param base the base token address, support ETH , WETH and other ERC20
    /// @return boolean Is support or not
    function isSupportedPair(address asset, address base)
        public
        virtual
        view
        override
        returns (bool)
    {
        return canResolvePrice(asset) && canResolvePrice(base);
    }

    /// @notice inherited from PriceGateway, to cal the asset price base on different token
    /// @dev For those outside contract , Please used this as the entry point
    /// @dev this Function will help to route the calculate to other cal function,
    /// @dev Please do not directly call the below cal function
    /// @param asset the asset token address, support ETH , WETH and other ERC20
    /// @param base the base token address, support ETH , WETH and other ERC20
    /// @param amount the amount of asset, in asset decimal
    /// @return uint256 the Asset Price in term of base token in base token decimal
    function assetPrice(
        address asset,
        address base,
        uint256 amount
    ) public view virtual override returns (uint256) {
        asset = asset == _WETH9() ? Denominations.ETH : asset;
        base = base == _WETH9() ? Denominations.ETH : base;
        // Feed Registry doesn't provide any WETH Price Feed, redirect to ETH case here

        if (asset == base) return amount;

        if (base == Denominations.USD) {
            return assetUSDPrice(asset, amount);
        }

        if (asset == Denominations.USD) {
            return usdAssetPrice(base, amount);
        }

        return derivedAssetPrice(asset, base, amount);
    }

    /// @notice Get Asset Price in Term of USD
    /// @dev Get the rate in Chainlink and scale the Price to decimal 8
    /// @param asset the asset token address, support ETH , WETH and other ERC20
    /// @param amount Asset Amount in term of asset's decimal
    /// @return uint256 the Asset Price in term of USD with hardcoded decimal 8
    function assetUSDPrice(address asset, uint256 amount)
        public
        view
        virtual
        returns (uint256)
    {
        if (asset == Denominations.USD) return amount;
        asset = asset == _WETH9() ? Denominations.ETH : asset;

        (
            uint80 roundID,
            int256 price,
            ,
            uint256 updatedAt,
            uint80 answeredInRound
        ) = FeedRegistryInterface(Constant.FEED_REGISTRY).latestRoundData(
                asset,
                Denominations.USD
            );
        uint8 priceDecimals = FeedRegistryInterface(Constant.FEED_REGISTRY)
            .decimals(asset, Denominations.USD);


        if (answeredInRound < roundID) {
            revert StaleRoundId(roundID, answeredInRound);
        }
        if (block.timestamp > updatedAt + Constant.STALE_PRICE_DELAY) {
            revert StaleTimestamp(block.timestamp, updatedAt);
        }

        price = scalePrice(
            price,
            priceDecimals,
            8 /* USD decimals */
        );

        if (price > 0) {
            // return price with decimal = price Decimal (8) + amount decimal (Asset decimal) - Asset decimal = price decimal(8)
            return
                (uint256(price) * amount) /
                10**assetDecimals(asset);
        }

        return 0;
    }

    /// @notice Get USD Price in term of Asset
    /// @dev Get the rate in Chainlink and scale the Price to asset decimal
    /// @param asset the asset token address, support ETH , WETH and other ERC20, used as base token address
    /// @param usdAmount Usd Amount with 8 decimal (arbitrum)
    /// @return uint256 the price by using asset as base with assets decimal
    function usdAssetPrice(address asset, uint256 usdAmount)
        public
        view
        virtual
        returns (uint256)
    {
        if (asset == Denominations.USD) return usdAmount;
        asset = asset == _WETH9() ? Denominations.ETH : asset;
        (
            uint80 roundID,
            int256 price,
            ,
            uint256 updatedAt,
            uint80 answeredInRound
        ) = FeedRegistryInterface(Constant.FEED_REGISTRY).latestRoundData(
                asset,
                Denominations.USD
            );
        uint8 priceDecimals = FeedRegistryInterface(Constant.FEED_REGISTRY)
            .decimals(asset, Denominations.USD);

        if (answeredInRound < roundID) {
            revert StaleRoundId(roundID, answeredInRound);
        }
        if (block.timestamp > updatedAt + Constant.STALE_PRICE_DELAY) {
            revert StaleTimestamp(block.timestamp, updatedAt);
        }

        price = scalePrice(
            price,
            priceDecimals,
            8 /* USD decimals */
        );
        if (price > 0) {
            // return price with decimal = 8 + asset Decimal - price Decimal (8) = asset Decimal
            return
                (usdAmount * (10**assetDecimals(asset))) /
                uint256(price);
        }

        return 0;
    }

    function derivedAssetPrice(
        address asset,
        address base,
        uint256 amount
    ) public view virtual returns (uint256) {
        int256 rate = getDerivedPrice(
            asset,
            base,
            18 /* ETH decimals */
        );

        if (rate > 0) {
            return
                uint256(
                    scalePrice(
                        int256(rate) * int256(amount),
                        18 + assetDecimals(asset),
                        assetDecimals(base)
                    )
                );
        }
        return 0;
    }

    function getDerivedPrice(
        address _base,
        address _quote,
        uint8 _decimals
    ) internal view virtual returns (int256) {
        if (_decimals <= uint8(0) || _decimals > uint8(18)) {
            revert InvaildDecimal(_decimals);
        }
        int256 decimals = int256(10**uint256(_decimals));
        (
            uint80 _baseRoundID,
            int256 basePrice,
            ,
            uint256 _baseUpdatedAt,
            uint80 _baseAnsweredInRound
        ) = FeedRegistryInterface(Constant.FEED_REGISTRY).latestRoundData(
                _base,
                Denominations.USD
            );

        if (_baseAnsweredInRound < _baseRoundID) {
            revert StaleRoundId(_baseAnsweredInRound, _baseRoundID);
        }
        if (block.timestamp > _baseUpdatedAt + Constant.STALE_PRICE_DELAY) {
            revert StaleTimestamp(block.timestamp, _baseUpdatedAt);
        }

        uint8 baseDecimals = FeedRegistryInterface(Constant.FEED_REGISTRY)
            .decimals(_base, Denominations.USD);
        basePrice = scalePrice(basePrice, baseDecimals, _decimals);
        (
            uint80 _quoteRoundID,
            int256 quotePrice,
            ,
            uint256 _quoteUpdatedAt,
            uint80 _quoteAnsweredInRound
        ) = FeedRegistryInterface(Constant.FEED_REGISTRY).latestRoundData(
                _quote,
                Denominations.USD
            );
        if (_quoteAnsweredInRound < _quoteRoundID) {
            revert StaleRoundId(_quoteAnsweredInRound, _quoteRoundID);
        }
        if (block.timestamp > _quoteUpdatedAt + Constant.STALE_PRICE_DELAY) {
            revert StaleTimestamp(block.timestamp, _quoteUpdatedAt);
        }

        uint8 quoteDecimals = FeedRegistryInterface(Constant.FEED_REGISTRY)
            .decimals(_quote, Denominations.USD);
        quotePrice = scalePrice(quotePrice, quoteDecimals, _decimals);

        return (basePrice * decimals) / quotePrice;
    }

    function scalePrice(
        int256 _price,
        uint8 _priceDecimals,
        uint8 _decimals
    ) internal pure virtual returns (int256) {

        if (_priceDecimals < _decimals) {
            return _price * int256(10**uint256(_decimals - _priceDecimals));
        } else if (_priceDecimals > _decimals) {
            return _price / int256(10**uint256(_priceDecimals - _decimals));
        }
        return _price;
    }

    function canResolvePrice(address asset) internal view returns (bool) {
        if (asset == Denominations.USD) return true;

        if (asset == _WETH9()) {
            // Feed Registry doesn't provide any WETH Price Feed, redirect to ETH case here
            asset = Denominations.ETH;
        }

        try
            FeedRegistryInterface(Constant.FEED_REGISTRY).getFeed(
                asset,
                Denominations.USD
            )
        {
            return true;
        } catch (bytes memory) {
            return false;
        }
    }

    function assetDecimals(address asset) public view virtual returns (uint8) {
        if (asset == Denominations.ETH) return 18;
        if (asset == Denominations.USD) return 8;
        try IERC20Metadata(asset).decimals() returns (uint8 _decimals) {
            return _decimals;
        } catch {
            return 0;
        }
    }

    function _WETH9() internal pure returns (address) {
        return Constant.WETH_ADDRESS;
    }

    uint256[50] private __gap;
}

contracts/TransferLiquidERC20BudgetAppro.content:
// SPDX-License-Identifier: GPL-3.0

pragma solidity 0.8.7;
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@chainlink/contracts/src/v0.8/Denominations.sol";

import "./lib/BytesLib.sol";
import "./base/CommonBudgetApproval.sol";
import "./base/PriceResolver.sol";
import "./interface/IBudgetApprovalExecutee.sol";

contract TransferLiquidERC20BudgetApproval is
    CommonBudgetApproval,
    PriceResolver
{
    using BytesLib for bytes;

    string public constant override name =
        "Transfer Liquid ERC20 Budget Approval";

    address private _baseCurrency;
    bool public allowAllAddresses;
    mapping(address => bool) public addressesMapping;
    address[] public tokens;
    mapping(address => bool) public tokensMapping;
    bool public allowAnyAmount;
    uint256 public totalAmount;

    // v2
    uint256[] public toTeamIds;
    mapping(uint256 => bool) public toTeamIdsMapping;

    event AllowTeam(uint256 indexed teamId);
    event ExecuteTransferLiquidERC20Transaction(
        uint256 indexed id,
        address indexed executor,
        address indexed toAddress,
        address token,
        uint256 amount
    );

    error AccountingSystemRequired();
    error InvalidAmountZero();
    error InvalidRecipient(address recipient);
    error InvalidToken(address _token);
    error AmountLimitExceeded();
    error UnresolvableToken(address _token);
    error TokenAlreadyAdded(address _token);
    error RecipientAlreadyAdded(address recipient);
    error TeamAlreadyAdded(uint256 teamId);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        InitializeParams calldata params,
        bool _allowAllAddresses,
        address[] memory _toAddresses,
        address[] memory _tokens,
        bool _allowAnyAmount,
        uint256 _totalAmount,
        address __baseCurrency,
        // v2
        uint256[] memory _toTeamIds
    ) external initializer {
        __BudgetApproval_init(params);

        allowAllAddresses = _allowAllAddresses;
        allowAnyAmount = _allowAnyAmount;
        totalAmount = _totalAmount;
        _baseCurrency = __baseCurrency;

        for (uint256 i = 0; i < _toAddresses.length; i++) {
            _addToAddress(_toAddresses[i]);
        }

        for (uint256 i = 0; i < _tokens.length; i++) {
            _addToken(_tokens[i]);
        }

        for (uint256 i = 0; i < _toTeamIds.length; i++) {
            _addToTeam(_toTeamIds[i]);
        }

        if (accountingSystem() == address(0)) {
            revert AccountingSystemRequired();
        }
    }

    function baseCurrency() public view override returns(address) {
        return _baseCurrency;
    }
    function accountingSystem() public view override returns(address) {
        return IBudgetApprovalExecutee(executee()).accountingSystem();
    }

    function executeParams() external pure override returns (string[] memory) {
        string[] memory arr = new string[](3);
        arr[0] = "address token";
        arr[1] = "address to";
        arr[2] = "uint256 value";
        return arr;
    }

    function _execute(uint256 transactionId, bytes memory data)
        internal
        override
    {
        (address token, address to, uint256 value) = abi.decode(
            data,
            (address, address, uint256)
        );
        uint256 amountInBaseCurrency;
        uint256 _totalAmount = totalAmount;
        bool _allowAnyAmount = allowAnyAmount;

        if (token == Denominations.ETH) {
            IBudgetApprovalExecutee(executee()).executeByBudgetApproval(
                to,
                "",
                value
            );
        } else {
            bytes memory executeData = abi.encodeWithSelector(
                IERC20.transfer.selector,
                to,
                value
            );
            IBudgetApprovalExecutee(executee()).executeByBudgetApproval(
                token,
                executeData,
                0
            );
        }

        amountInBaseCurrency = assetBaseCurrencyPrice(token, value);
        if (!allowAllAddresses && !addressesMapping[to] && !_checkIsToTeamsMember(to)) {
            revert InvalidRecipient(to);
        }
        if (!tokensMapping[token]) {
            revert InvalidToken(token);
        }
        if (amountInBaseCurrency == 0) {
            revert InvalidAmountZero();
        }

        if (!_allowAnyAmount) {
            if (amountInBaseCurrency > _totalAmount) {
                revert AmountLimitExceeded();
            }
            totalAmount = _totalAmount - amountInBaseCurrency;
        }
        emit ExecuteTransferLiquidERC20Transaction(
            transactionId,
            msg.sender,
            to,
            token,
            value
        );
    }

    function _addToken(address token) internal {
        if (tokensMapping[token]) {
            revert TokenAlreadyAdded(token);
        }
        if (!canResolvePrice(token)) {
            revert UnresolvableToken(token);
        }

        tokens.push(token);
        tokensMapping[token] = true;
        emit AllowToken(token);
    }

    function _addToAddress(address to) internal {
        if (addressesMapping[to]) {
            revert RecipientAlreadyAdded(to);
        }
        addressesMapping[to] = true;
        emit AllowAddress(to);
    }

    function tokensLength() public view returns (uint256) {
        return tokens.length;
    }

    function _checkIsToTeamsMember(address to) internal view returns (bool) {
        uint256 _toTeamIdsLength = toTeamIds.length;
        address[] memory toArray = new address[](_toTeamIdsLength);
        for (uint256 i = 0; i < _toTeamIdsLength; i++) {
            toArray[i] = to;
        }

        uint256[] memory balances = ITeam(team()).balanceOfBatch(
            toArray,
            toTeamIds
        );

        for (uint256 i = 0; i < balances.length; i++) {
            if (balances[i] > 0) {
                return true;
            }
        }
        return false;
    }

    function _addToTeam(uint256 teamId) internal {
        if (toTeamIdsMapping[teamId]) {
            revert TeamAlreadyAdded(teamId);
        }
        toTeamIdsMapping[teamId] = true;
        toTeamIds.push(teamId);
        emit AllowTeam(teamId);
    }

    function toTeamsLength() public view returns (uint256) {
        return toTeamIds.length;
    }
}

contracts/DaoBeacon.sol.content:
// SPDX-License-Identifier: GPL-3.0

pragma solidity 0.8.7;
import "@openzeppelin/contracts/utils/Address.sol";
import "./lib/Constant.sol";


contract DaoBeacon {
    using Address for address;

    string public name;
    mapping(bytes32 => address) public implementation;

    struct ContractImpl {
        bytes32 contractName;
        address impl;
    }

    event DaoBeaconInit(string name, ContractImpl[] contractImpl);
    error InvalidContract(address _contract);
    error DaoImplementationNotExist();
    constructor(string memory _name, ContractImpl[] memory _contractImpl) {
        name = _name;
        for (uint256 i; i < _contractImpl.length; i++) {
            ContractImpl memory _ci = _contractImpl[i];
            address _impl = _ci.impl;
            if (!_impl.isContract()) {
                revert InvalidContract(_impl);
            }
            implementation[_ci.contractName] = _impl;
        }
        if (implementation[Constant.BEACON_NAME_DAO] == address(0)) {
            revert DaoImplementationNotExist();
        }
        emit DaoBeaconInit(name, _contractImpl);
    }
}

