    function isSupportedPair(address asset, address base)
        public
        view
        returns (bool)
    {        

        if (asset == base && asset != address(0)) return true;
        return 
            tokenPairPriceGatewayMap[asset][base] != address(0) ||
            IPriceGateway(defaultPriceGateway).isSupportedPair(asset, base);
    }

